// robot-list.component.ts
import { Component, OnInit } from '@angular/core';
import { RobotService } from './robot.service';

@Component({
  selector: 'app-robot-list',
  templateUrl: './robot-list.component.html',
  styleUrls: ['./robot-list.component.css']
})
export class RobotListComponent implements OnInit {
  robots: any[] = []; // Puedes ajustar el tipo según la estructura real de tu objeto Robot

  constructor(private robotService: RobotService) { }

  ngOnInit(): void {
    this.loadRobots();
  }

  private loadRobots(): void {
    this.robotService.getAllRobots().subscribe(
      (robots: any[]) => {
        this.robots = robots;
      },
      (error) => {
        console.error('Error al cargar la lista de robots', error);
      }
    );
  }
}
