// login.component.ts
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AdminService } from '../services/admin.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  user: string = '';
  password: string = '';
  isLogged: boolean = false;  // Cambiado de 'isLoggedIn'

  constructor(private adminService: AdminService, private router: Router) { }

  login(selectedRole: string): void {
    this.adminService.login(this.user, this.password).subscribe(
      response => {
        this.isLogged = true;
        this.adminService.setSelectedRole(selectedRole);
        this.router.navigate(['/home']);
      },
      error => {
        console.error('Error en el login:', error);
      }
    );
  }
}

<!-- login.component.html -->

<div class="login-container">
  <form class="login-form" (ngSubmit)="login()">
    <div>
      <label for="username">Usuario:</label>
      <input type="text" id="username" name="username" [(ngModel)]="user">
    </div>
    <div>
      <label for="password">Contraseña:</label>
      <input type="password" id="password" name="password" [(ngModel)]="password">
    </div>

    <!-- Nuevo elemento para seleccionar el rol -->
    <div>
      <label for="role">Seleccionar Rol:</label>
      <select id="role" name="role" [(ngModel)]="selectedRole">
        <option value="medico">Médico</option>
        <option value="tecnico">Técnico</option>
      </select>
    </div>

    <button type="submit" [disabled]="isLoggedIn">Iniciar sesión</button>
  </form>
</div>
