// login.component.ts
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AdminService } from '../services/admin.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  user: string = '';
  password: string = '';

  constructor(private adminService: AdminService, private router: Router) { }

  login(): void {
    this.adminService.login(this.user, this.password).subscribe(
      response => {
        // Manejar la respuesta del backend (por ejemplo, almacenar token de sesión)
        // Redirigir a la página principal u otra página después del login
        this.router.navigate(['/home']);
      },
      error => {
        // Manejar el error, por ejemplo, mostrar un mensaje de error al usuario
        console.error('Error en el login:', error);
        // Puedes mostrar un mensaje de error en la interfaz de usuario si lo prefieres
      }
    );
  }
}
