import { Component } from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import { RequestService } from '../services/request.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Request } from '../request';
import { Location } from '@angular/common';
import { MedicalService } from '../services/medical.service';

@Component({
  selector: 'app-create-solicitud',
  templateUrl: './create-solicitud.component.html',
  styleUrls: ['./create-solicitud.component.css']
})
export class CreateSolicitudComponent {

  request: Request = {
    id: undefined,
    drugs: '',
    route: '',
    medico: undefined,
    accepted: false
  };
  create: boolean = true;
  requestId: number = 0;

  constructor(
    private requestService: RequestService,
    private medicalService: MedicalService,
    private snackbar: MatSnackBar,
    private router: Router,
    private route: ActivatedRoute,
    private _location: Location
  ) { }

  ngOnInit(): void {
    if (this.route.snapshot.paramMap.has('id')) {
      this.requestId = Number(this.route.snapshot.paramMap.get('id'));
      this.create = false;
    }

    if (!this.create) {
      this.requestService.getRequest(this.requestId).subscribe({
        next: (data: any) => {
          this.request = data; // Assuming the response structure matches Request
        },
        error: (e: any) => {
          this.snackbar.open('Error communicating with the server', '', {
            duration: 3000
          });
        }
      });
    }
  }

  back() {
    this._location.back();
  }

  onSubmit(request: Request) {
    if (this.create) {
      const medicoId = request.medico;

      if (typeof medicoId === 'number') {
        this.medicalService.getMedicalById(medicoId).subscribe({
          next: (medicoData: any) => {
            request.medico = medicoData;
            this.requestService.createRequest(request).subscribe({
              next: () => {
                this.router.navigateByUrl('/request');
                this.snackbar.open('Solicitud created ', '', {
                  duration: 3000
                });
              },
              error: (e: any) => {
                this.snackbar.open('Error creating the solicitud: ' + e.error, '', {
                  duration: 3000
                });
              }
            });
          },
          error: (error: any) => {
            this.snackbar.open('Error fetching medical professional: ' + error, '', {
              duration: 3000
            });
          }
        });
      } else {
        this.snackbar.open('Error: Medico ID is not a number', '', {
          duration: 3000
        });
      }
    } else {
      this.requestService.updateRequest(request).subscribe({
        next: () => {
          this.router.navigateByUrl('/request');
          this.snackbar.open('Solicitud updated ', '', {
            duration: 3000
          });
        },
        error: (e: any) => {
          this.snackbar.open('Error updating the solicitud: ' + e.error, '', {
            duration: 3000
          });
        }
      });
    }
  }
}
<div class="container">
    <mat-card>
        <mat-card-title>
            <div *ngIf="create">Crear Solicitud</div>
            <div *ngIf="!create">Editar Solicitud {{request.id}}</div>
        </mat-card-title>
        
        <form class="create-model-form" #form="ngForm" (ngSubmit)="onSubmit(form.value)" autocomplete="off">
            <mat-form-field *ngIf="!create" class="full-width">
                <input matInput type="number"  disabled name="id" [(ngModel)]="request.id">
            </mat-form-field>

            <mat-form-field class="full-width">
                <input matInput type="string" placeholder="Destino" required name="ruta"  [(ngModel)]="request.route">
            </mat-form-field>

            <mat-form-field class="full-width">
                <input matInput type="string" placeholder="Medicamentos" required name="medicamento"  [(ngModel)]="request.drugs">
            </mat-form-field>

            <mat-form-field class="full-width">
                <input matInput type="number" placeholder="ID Médico" required name="idMedico"  [(ngModel)]="request.medico.id">
            </mat-form-field>

            <div class="row-buttons">
                <span class="spacer"></span>
                <a (click)="back()" class="form-btn" mat-button>Atras</a>
                <button type="submit" class="form-btn" [disabled]="form.invalid" mat-sraised-button color="accent">
                <div *ngIf="create">Crear</div>
                <div *ngIf="!create">Editar</div>
                </button>
            </div>          
        </form> 
    </mat-card>
</div>
