// robot-list.component.ts
import { Component, OnInit } from '@angular/core';
import { RobotService } from '../services/robot.service';

@Component({
  selector: 'app-robots',
  templateUrl: './robots.component.html',
  styleUrls: ['./robots.component.css']
})
export class RobotListComponent implements OnInit {
  robots: any[] = []; 

  constructor(private robotService: RobotService) { }

  ngOnInit(): void {
    this.loadRobots();
  }

  private loadRobots(): void {
    this.robotService.getAllRobots().subscribe(
        (robots: any[]) => {
            console.log('Respuesta del servicio:', robots);
            this.robots = robots;
        },
        (error) => {
            console.error('Error al cargar la lista de robots', error);
        }
    );
}
}

<div>
  <h2>Lista de Robots</h2>
  <ul *ngIf="robots.length > 0">
      <li *ngFor="let robot of robots">
          Nombre: {{ nombre.id }} - Estado: {{ robot.estado }}
      </li>
  </ul>
  <p *ngIf="robots.length === 0">No hay robots disponibles.</p>
</div>
